import React, { useState } from "react";
import classes from "./AddUser.module.css";

const AddUser = (props) => {
  const [enteredInput, setEnteredInput] = useState(null);

  const submitHandler = (event) => {
    event.preventDefault();
    props.onValidate(enteredInput);
  };

  const inputChangeHandler = (identifier, value) => {
    setEnteredInput((prevState) => {
      return { ...prevState, [identifier]: value };
    });
  };

  return (
    <div>
      <form className={classes.form} onSubmit={submitHandler}>
        <p>
          <label className={classes.label}>User name</label>
          <input
            onChange={(event) =>
              inputChangeHandler("userName", event.target.value)
            }
          />
        </p>
        <p>
          <label className={classes.label}>Age</label>
          <input
            onChange={(event) => inputChangeHandler("age", event.target.value)}
          />
        </p>
        <button>Add User</button>
      </form>
    </div>
  );
};

export default AddUser;
