import React, { useState } from "react";
import AddUser from "./components/UserInput/AddUser";
import UserList from "./components/UserInput/UserList";
import ErrorModal from "./components/ErrorModal/ErrorModal";

const usersData = [];

function App() {
  const [userInput, setUserInput] = useState(null);
  var isValid;

  const validationHandler = (input) => {
    console.log("App.js");
    console.log(input);
    setUserInput(input);
  };

  if (
    userInput &&
    typeof userInput["userName"] === "string" &&
    userInput["age"] > 0
  ) {
    console.log("userInput is valid");
    isValid = true;
    usersData.push({ userName: userInput["userName"], age: userInput["age"] });
    console.log(usersData);
  }

  return (
    <div>
      <AddUser onValidate={validationHandler} />
      {/* conditionally returned       */}
      {!isValid && <ErrorModal />}
      {isValid && <UserList users={usersData} />}
    </div>
  );
}

export default App;
