import React, { useState } from "react";
import AddUser from "./components/UserInput/AddUser";
import UsersList from "./components/UserInput/UsersList";
import ErrorModal from "./components/ErrorModal/ErrorModal";

const usersData = [];

function App() {
  const [isValid, setIsValid] = useState(false);

  const validationHandler = (userInput) => {};

  return (
    <div>
      <AddUser onValidate={validationHandler} />
      {/* conditionally returned       */}
      {!isValid && <ErrorModal />}
      {isValid && <UsersList users={usersData} />}
    </div>
  );
}

export default App;
